import React from "react";
import { Link, withRouter } from "react-router-dom";
import {
  Container,
  Row,
  Col,
  Card,
  CardBody,
  FormInput,
  FormGroup,
  FormCheckbox,
  FormFeedback,
  Form,
  Button,
  Alert,
} from "shards-react";
import validations from "../utils/ValidationsUtil";
import constants from "../utils/Constants";
import "../styles/login.css";
var _ = require('lodash');

const errorsDefault = {
  email: {
    required: false
  },
  password: {
    required: false
  }
}

class Login extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      errors: errorsDefault,
      remember: false,
      showAlert: true
    }

    this.toogleCheckbox.bind(this);
    this.login.bind(this);
    this.invalidateError.bind(this);
  };

  toogleCheckbox() {
    this.setState({
      remember: !this.state.remember
    })
  }

  login(e) {
    e.preventDefault()
    e.persist();
    const errors = _.clone(errorsDefault);
    errors.email.required = !validations.validateRequired(e.target.email.value);
    errors.password.required = !validations.validateRequired(e.target.password.value);

    if (Object.values(errors).find(fieldValidations => Object.values(fieldValidations).find(value => value))) {  // Si alguno de los errores est√° en true...
      this.setState({
        errors: errors
      })
    } else {
      console.log(e.target.email.value);
      console.log(e.target.password.value);
      console.log("remember me: ", this.state.remember)

      //TODO call service
      const loggedIn = false
      if (loggedIn) {
        this.props.history.push("/home");
      } else {
        this.setState({
          showAlert: true
        })
        setTimeout(() => {
          this.setState({
            showAlert: false
          })
        }, 4000);
      }
    }
  }

  invalidateError(field) {
    let errors = this.state.errors;
    Object.keys(errors[field]).forEach(validationType => errors[field][validationType] = false)
    this.setState({
      errors: errors
    })
  }

  render() {
    return (
      <Container fluid className="main-content-container px-4 h-100">
        <Row noGutters className="h-100">
          <Col className="mx-auto login">
            <Card small>
              <CardBody className="px-4">
                {/* <img class="login__logo d-table mx-auto mb-3" src="../images/shards-dashboards-logo.svg" alt="Login Template" /> */}
                <h5 className="login__titulo text-center mt-5 mb-4">Ingresa a tu cuenta</h5>
                <Form onSubmit={e => this.login(e)} noValidate>
                  <FormGroup>
                    <label htmlFor="email">Email</label>
                    <FormInput
                      type="email"
                      id="email"
                      placeholder="Direcci√≥n de correo"
                      autoComplete="email"
                      invalid={this.state.errors.email.required}
                      onChange={() => this.invalidateError("email")}
                    />
                    <FormFeedback>Campo requerido</FormFeedback>
                  </FormGroup>
                  <FormGroup>
                    <label htmlFor="password">Contrase√±a</label>
                    <FormInput 
                      id="password" 
                      type="password" 
                      placeholder="Contrase√±a"
                      invalid={this.state.errors.password.required}
                      onChange={() => this.invalidateError("password")} />
                    <FormFeedback>Campo requerido</FormFeedback>
                  </FormGroup>
                  <FormGroup>
                    <FormCheckbox checked={this.state.remember} onChange={() => this.toogleCheckbox()}>
                      Recordarme
                    </FormCheckbox>
                  </FormGroup>
                  <Button type="submit" pill className="d-table mx-auto mb-5">Iniciar Sesi√≥n</Button>
                  <FormGroup>
                    <Alert className="mb-3" open={this.state.showAlert} theme={constants.Themes.ERROR}>
                      Los datos ingresados no son correctos. Intent√° de nuevo
                    </Alert>
                  </FormGroup>
                </Form>
              </CardBody>
            </Card>
            <div className="px-2 pt-3 login__justify_links">
              <Link to="/forgot-password">Olvidaste tu contrase√±a?</Link>
              <Link to="/signup">Crea una cuenta nueva</Link>
            </div>
          </Col>
        </Row>
      </Container>
    );
  }
}

export default withRouter(Login);
