import React from "react";
import {
  Container,
  Row,
  Col,
  Card,
  CardHeader,
  ListGroup,
  ListGroupItem,
  Form,
  FormCheckbox,
  FormInput,
  InputGroup,
  InputGroupAddon,
  InputGroupText
} from "shards-react"
import PageTitle from "./../components/common/PageTitle";
//services
import UsersService from "./../api/services/usersService";

export default class Alerts extends React.Component {
  constructor(props) {
    super(props)
    this.state = {
      vehicles: []
    }
  }

  async loadVehicles() {
    const usersService = new UsersService();
    const vehicles = await usersService.getVehiclesByUserID(10);
    this.setState({
      vehicles: vehicles
    });
  }

  componentWillMount() {
    this.loadVehicles();
  }

  render() {
    return (
      <Container fluid className="main-content-container px-4">
        {/* Page Header */}
        <Row noGutters className="page-header py-4">
          <PageTitle title="Alertas" className="text-sm-left mb-3 col-sm-12" />
        </Row>

        <Row>
          <Col>
            {this.state.vehicles.map((vehicle) => (
              <Card key={vehicle.id} small className="mb-4">
                <CardHeader className="border-bottom">
                  <h6 className="m-0">{`${vehicle.type} - ${vehicle.plate}`}</h6>
                </CardHeader>
                <ListGroup flush>
                  <ListGroupItem className="px-3">
                    <Form>
                      <strong className="text-muted d-block mb-3">
                        Alarma de movimiento
                      </strong>
                      <FormCheckbox toggle small>
                        Default
                      </FormCheckbox>

                      <strong className="text-muted d-block mb-3">
                        Alarma de velocidad
                      </strong>
                      <FormCheckbox toggle small>
                        Default
                      </FormCheckbox>
                      <InputGroup className="mb-3">
                        <InputGroupAddon type="prepend">
                          <i class="material-icons">map</i>
                          {/* <InputGroupText>@</InputGroupText> */}
                        </InputGroupAddon>
                        <FormInput placeholder="Velocidad mÃ¡xima" />
                      </InputGroup>
                    </Form>
                  </ListGroupItem>
                </ListGroup>
              </Card>
            ))}
          </Col>
        </Row>
      </Container>
    )
  }
}
