import React from "react";
import { Link } from "react-router-dom";
import {
  Container,
  Row,
  Col,
  Card,
  CardBody,
  FormInput,
  FormFeedback,
  Form,
  Button,
} from "shards-react";

import "../styles/register.css";

const initialValues = {
  name: true,
  lastname: true,
  email: true,
  tel: true,
  password: true,
  repeatPassword: true,
  address: true,
  zipcode: true
}

export default class Register extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      isValid: initialValues
    }

    this.createAccount.bind(this);
  };

  createAccount(e) {
    e.preventDefault()
    e.persist();
    const name = e.target.name.value;
    const lastname = e.target.lastname.value;
    const email = e.target.email.value;
    const tel = e.target.tel.value;
    const password = e.target.password.value;
    const repeatPassword = e.target.repeatPassword.value;
    const address = e.target.address.value;
    const zipcode = e.target.zipcode.value;
    this.validateFields(name, lastname, email, tel, password, repeatPassword, address, zipcode);
  }

  validateFields(name, lastname, email, tel, password, repeatPassword, address, zipcode) {
    if (!name) {
      this.setState({
        isValid: { name: false }
      });
    }
  }

  render() {
    return (
      <Container fluid className="main-content-container px-4 h-100">
        <Row noGutters className="h-100">
          <Col className="mx-auto register">
            <Card small>
              <CardBody className="px-4">
                <h5 className="register__titulo text-center mt-5 mb-4">Crea una cuenta nueva</h5>
                <Form onSubmit={(e) => this.createAccount(e)} noValidate>
                  <Row form>
                    {/* Nombre */}
                    <Col md="6" className="form-group">
                      <label htmlFor="name">Nombre</label>
                      <FormInput
                        id="name"
                        placeholder="Nombre"
                        required
                        valid={this.state.isValid.name}
                        // onChange={() => { }}
                      />
                      <FormFeedback valid={!this.state.isValid.name}>Campo requerido</FormFeedback>
                    </Col>
                    {/* Apellido */}
                    <Col md="6" className="form-group">
                      <label htmlFor="lastname">Apellido</label>
                      <FormInput
                        id="lastname"
                        placeholder="Apellido"
                        required
                        // onChange={() => { }}
                      />
                    </Col>
                  </Row>
                  <Row form>
                    {/* Email */}
                    <Col md="7" className="form-group">
                      <label htmlFor="email">Email</label>
                      <FormInput
                        type="email"
                        id="email"
                        placeholder="Dirección de correo"
                        required
                        // onChange={() => { }}
                        autoComplete="email"
                      />
                    </Col>
                    {/* Teléfono */}
                    <Col md="5" className="form-group">
                      <label htmlFor="tel">Teléfono</label>
                      <FormInput
                        type="tel"
                        id="tel"
                        placeholder="Teléfono"
                        required
                        // onChange={() => { }}
                      />
                    </Col>
                  </Row>
                  <Row form>
                    {/* Contraseña */}
                    <Col md="6" className="form-group">
                      <label htmlFor="password">Contraseña</label>
                      <FormInput
                        type="password"
                        id="password"
                        placeholder="Contraseña"
                        required
                        // onChange={() => { }}
                        autoComplete="current-password"
                      />
                    </Col>
                    {/* Repetir contraseña */}
                    <Col md="6" className="form-group">
                      <label htmlFor="repeatPassword">Repetir contraseña</label>
                      <FormInput
                        type="password"
                        id="repeatPassword"
                        placeholder="Repetir contraseña"
                        required
                        // onChange={() => { }}
                        // autoComplete="current-password"
                      />
                    </Col>
                  </Row>
                  <Row form>
                    {/* Dirección */}
                    <Col md="9" className="form-group">
                      <label htmlFor="address">Dirección</label>
                      <FormInput
                        id="address"
                        placeholder="Dirección"
                        required
                        // onChange={() => { }}
                      />
                    </Col>
                    {/* Zip Code */}
                    <Col md="3" className="form-group">
                      <label htmlFor="zipcode">CP</label>
                      <FormInput
                        type="number"
                        id="zipcode"
                        placeholder="CP"
                        required
                        // onChange={() => { }}
                      />
                    </Col>
                  </Row>
                  <Row>
                    <Button className="ml-auto" type="submit" theme="accent">Registrarse</Button>
                  </Row>
                </Form>
              </CardBody>
            </Card>
            <div className="px-2 pt-3 register__justify_links">
              <Link to="/forgot-password">Olvidaste tu contraseña?</Link>
              <Link to="/signin">Volver al login</Link>
            </div>
          </Col>
        </Row>
      </Container>
    );
  }
}
